mapper.select:
SELECT 

FROM cultural_couplets a

WHERE a.id = #{id}

SELECT
FROM cultural_couplets a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.lexicon_id = #{lexicon.id}
AND a.size_id LIKE '%'||#{size.id}||'%' '%'+#{size.id}+'%' concat('%',#{size.id},'%')
AND a.frame_id LIKE '%'||#{frame.id}||'%' '%'+#{frame.id}+'%' concat('%',#{frame.id},'%')
AND a.craft_id LIKE '%'||#{craft.id}||'%' '%'+#{craft.id}+'%' concat('%',#{craft.id},'%')
AND a.recommend = #{recommend}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cultural_couplets a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cultural_couplets SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select count(*) from cultural_couplets a
LEFT JOIN cultural_lexicon lexicon ON lexicon.id = a.lexicon_id
where lexicon.type= #{type}
