mapper.select:
SELECT 

FROM cultural_painting a

WHERE a.id = #{id}

SELECT 

FROM cultural_painting a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.author_id = #{author.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cultural_painting a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cultural_painting SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
