SELECT 

FROM cultural_finished_order a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.type = #{type}


		AND a.finished_name = #{finishedName}


		AND a.customer_id = #{customer.id}


		AND a.installer_id = #{installer.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cultural_finished_order a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


